func threeSum(nums []int) [][]int {
	all := make(map[int]int)
	for _, v := range nums {
		all[v]++
	}

	triples := make(map[string][]int)
	for v1, v1Count := range all {
		for v2, v2Count := range all {
			n1 := v1Count
			n2 := v2Count
			if v1 == v2 {
				n1--
				n2--
			}
			if n1 > 0 {
				twoSum := v1 + v2
				v3 := -twoSum

				if v1 == v3 {
					n1--
				}
				if v2 == v3 {
					n2--
				}

				if n1 > 0 && n2 > 0 && all[v3] > 0 {
					triple := []int{v1, v2, v3}
					sort.Slice(triple, func(i, j int) bool {
						return triple[i] < triple[j]
					})
					triples[fmt.Sprintf("%d_%d_%d", triple[0], triple[1], triple[2])] = triple
				}

			}
		}
	}

	res := make([][]int, len(triples))
	i := 0
	for _, v := range triples {
		res[i] = v
		i++
	}

	return res
}
